'use strict';
class PubSub {

  /**
   * Creates handlers
   */
  constructor() {
    this.handlers = [];
  }

  /**
   * @param {String} event
   * @param {Method} handler
   * @param {HTMLElement} context
   */
  subscribe(event, handler, context) {
    if (typeof context === 'undefined') {
      context = handler;
    }
    this.handlers.push({event: event, handler: handler.bind(context)});
  }

  /**
   * @param {String} event
   * @param {String|Number|Boolean|Object|Array} change
   */
  publish(event, change) {
    for (let i = 0; i < this.handlers.length; i++) {
      if (this.handlers[i].event === event) {
        let oldValue = this.handlers[i].oldValue;
        // dirty checking value, ensures that we don't create a loop
        if (oldValue !== change.value) {
          this.handlers[i].handler(change, this.handlers[i].oldValue);
          this.handlers[i].oldValue = change.value;
        }
      }
    }
  }
}

export default window.PubSub ? window.PubSub : window.PubSub = new PubSub();

'use strict';
let sheduled = false;
const afterRenderQue = [];
const beforeRenderQue = [];

const callMethod = array => {
  const context = array[0];
  const callback = array[1];
  const args = array[2];
  try {
    callback.apply(context, args);
  } catch(e) {
    setTimeout(() => {
      throw e;
    })
  }
};

const flushQue = que => {
  while (que.length) {
    callMethod(que.shift);
  }
};

const runQue = que => {
  for (let i=0, l=que.length; i < l; i++) {
    callMethod(que.shift());
  }
  sheduled = false;
}

const shedule = () => {
  sheduled = true;
  requestAnimationFrame(() => {
    flushQue(beforeRenderQue);
    setTimeout(() => {
      runQue(afterRenderQue);
    });
  });
};

export default {
  afterRender: (context, callback, args) => {
    if (!sheduled) {
      shedule();
    }
    afterRenderQue.push([context, callback, args]);
  },
  beforeRender: (context, callback, args) => {
    if (!sheduled) {
      shedule();
    }
    beforeRenderQue.push([context, callback, args]);
  }

}

