export default (fills, root='bower_components') => {
  return new Promise((resolve, reject) => {
    fills = ensureArray(fills);
    fills = fills.map(fill => {
      let name;
      let main = null;
      if (typeof(fill) === 'object') {
        name = fill.name;
        main = fill.main;
